<template>
    <v-app>
        <v-navigation-drawer
                app
                temporary
                v-model="drawer"
        >
            <v-list>
                <v-list-item
                        :to="link.url"
                        v-for="link of links"
                        :key="link.title"
                >
                    <v-list-item-icon>
                        <v-icon>{{link.icon}}</v-icon>
                    </v-list-item-icon>

                    <v-list-item-content>
                        <v-list-item-title v-text="link.title"/>
                    </v-list-item-content>
                </v-list-item>
                <v-list-item
                        @click="onLogout"
                        v-if="isUserLoggedIn"
                >
                    <v-list-item-icon>
                        <v-icon>exit_to_app</v-icon>
                    </v-list-item-icon>

                    <v-list-item-content>
                        <v-list-item-title v-text="'Logout'"/>
                    </v-list-item-content>
                </v-list-item>
            </v-list>
        </v-navigation-drawer>

        <v-app-bar app dark color="primary">
            <v-app-bar-nav-icon
                    @click="drawer = !drawer"
                    class="hidden-md-and-up"
            />
            <v-toolbar-title>
                <!--<router-link to="/" tag="span" class="pointer"/>-->
            </v-toolbar-title>
            <a
                    class="header__logo"
                    target="_blank"
                    href="/"
                    :style="{
                    background: 'url(' + logoUrl + ') no-repeat 50%',
                    backgroundSize: 'contain' + ' '
                }"
            />
            <v-spacer></v-spacer>
            <v-toolbar-items class="hidden-sm-and-down">
                <v-btn
                        text
                        v-for="link in links"
                        :key="link.title"
                        :to="link.url"
                >
                    <v-icon left>{{link.icon}}</v-icon>
                    {{link.title}}
                </v-btn>
                <v-btn
                        text
                        @click="onLogout"
                        v-if="isUserLoggedIn"
                >
                    <v-icon left>exit_to_app</v-icon>
                    Logout
                </v-btn>
            </v-toolbar-items>
        </v-app-bar>

        <!-- Sizes your content based upon application components -->
        <v-main>
            <!-- If using vue-router -->
            <router-view/>
        </v-main>
    </v-app>
</template>

<script lang="ts">
import Vue from 'vue';

export default Vue.extend({
    data: () => ({
        drawer: false,
        //eslint-disable-next-line max-len
        logoUrl: 'data:image/svg+xml;base64,',
    }),
    computed: {
        isUserLoggedIn() {
            return this.$store.getters['user/isUserLoggedIn'];
        },
        links() {
            return [
                {title: 'Login', icon: 'lock', url: '/login'},
                {title: 'Registration', icon: 'face', url: '/registration'},
            ];
        },
    },
    methods: {
        onLogout() {
            this.$store.dispatch('logoutUser');
            this.$router.push('/');
        },
    },
});
</script>

<style scoped lang="scss">
    .header__logo {
        width: 150px;
        height: 40px;
        min-width: 84px;
        display: inline-block;
        font-size: 0;
        line-height: 0;
        text-decoration: none;
        margin-right: 6px;
    }
</style>
